args:
FILE="${file}"; WF="${workspaceFolder}/Jiggly Jam"; BASENAME="${fileBasenameNoExtension}"; 
case "$FILE" in 
  *"/Source_code/Tests/"*|*"/Tests/"*|*"/tests/"*) OUT="$WF/build/test"; FLAGS="-g" ;; 
  *) OUT="$WF/build/final"; FLAGS="-O2" ;; 
esac; 
mkdir -p "$OUT"; cd "$WF"; 
# compile
g++ -std=c++17 $FLAGS -I. "$FILE" Libraries/Gmae_physics/jelly.cpp Libraries/Controll_system/BodyControlSystem.cpp Libraries/Controll_system/InputManager.cpp Libraries/Player/Player.cpp -lsfml-graphics -lsfml-window -lsfml-system -o "$OUT/$BASENAME"

FILE="${file}"; WF="${workspaceFolder}/Jiggly Jam"; BASENAME="${fileBasenameNoExtension}"; 
case "$FILE" in 
  *"/Source_code/Tests/"*|*"/Tests/"*|*"/tests/"*) OUT="$WF/build/test" ;; 
  *) OUT="$WF/build/final" ;; 
esac; 
exec "$OUT/$BASENAME"
